import React, { useState, useEffect } from 'react';
import {useSelector, useDispatch} from 'react-redux'
import { Link } from 'react-router-dom';
import { SidebarContent, SidebarHeader } from './styles';
import { FaCog, FaStore, FaCalendar, FaUser, FaHome } from 'react-icons/fa';
import {
  MdStore,
  MdRoomService,
  MdRestaurantMenu,
  MdTablet,
  MdMenu,
} from 'react-icons/md';
import { alteraFuncionamentoRequest } from '~/store/modules/user/actions';

import Tooltip from 'react-bootstrap/Tooltip'
import Overlay from 'react-bootstrap/Overlay'
import OverlayTrigger from 'react-bootstrap/OverlayTrigger'

export default function Sidebar() {


  const [collapsed, setCollapsed] = useState(false);


  const profile = useSelector(state => state.auth.profile);
  const dispatch = useDispatch()



  const handleCollapse = () => {

    setCollapsed(!collapsed)
    sessionStorage.removeItem('collapsedSidebar');

    sessionStorage.setItem('collapsedSidebar', !collapsed)

  }

  function renderToolTip(props){
    return(
      <Tooltip>
          {props}
      </Tooltip>
    )
  }



  return (
    <>
      <SidebarContent className={`${collapsed ? 'collapsed' : ''}`}>
        <SidebarHeader className="sidebar-header">

          <button
            type="button"
            className="btn-link"
            onClick={() => handleCollapse()}
          >
            <MdMenu />
          </button>
        </SidebarHeader>

            <OverlayTrigger placement="right" delay={{show: 250, hide: 150}} overlay={renderToolTip('Dashboard')}>
                <Link to="/dashboard">
                  <FaHome /> <span className="sidebar-item-text">Dashboard</span>
                </Link>
            </OverlayTrigger>



            <OverlayTrigger placement="right" delay={{show: 250, hide: 150}} overlay={renderToolTip('Users')}>
                <Link to="/dashboard">
                  <FaUser /> <span className="sidebar-item-text">Users</span>
                </Link>
            </OverlayTrigger>


            <OverlayTrigger placement="right" delay={{show: 250, hide: 150}} overlay={renderToolTip('Settings')}>
                <Link to="/dashboard">
                  <FaCog /> <span className="sidebar-item-text">Settings</span>
                </Link>
            </OverlayTrigger>







      </SidebarContent>
    </>
  );
}
