import React, { useState } from 'react';

// import { Container } from './styles';

function Post({post}) {
    const [selected, setSelected] = useState(false)
  return (
      <div className={`post   ${selected && 'selected'}`} onClick={setSelected(!selected)}>
          <div className="post_user-image">
              <img src={post.user.url_picture} alt={post.user.name} />
          </div>
          <div className="post_content">
              <div className="post_content-header">
                  <span className="user_name">{post.user.name}</span>
                  <span className="user_name"></span>
              </div>
          </div>
      </div>
  );
}

export default Post;
